function grayscaleImage(imgObj) {
	if(!$.browser.msie){
		if (imgObj) {
			var canvas = document.createElement('canvas');
			var canvasContext = canvas.getContext('2d');
			
			var imgW = imgObj.width;
			var imgH = imgObj.height;
			
			canvas.width = imgW;
			canvas.height = imgH;
			
			canvasContext.drawImage(imgObj, 0, 0);
			var imgPixels = canvasContext.getImageData(0, 0, imgW, imgH);
			
			for(var y = 0; y < imgPixels.height; y++){
				for(var x = 0; x < imgPixels.width; x++){
					var i = (y * 4) * imgPixels.width + x * 4;
					var avg = (imgPixels.data[i] + imgPixels.data[i + 1] + imgPixels.data[i + 2]) / 3;
					imgPixels.data[i] = avg; 
					imgPixels.data[i + 1] = avg; 
					imgPixels.data[i + 2] = avg;
				}
			}
			
			canvasContext.putImageData(imgPixels, 0, 0, 0, 0, imgPixels.width, imgPixels.height);
			return canvas.toDataURL();
		}
	} else {
		//return imgObj.src;	
	}
}


// Fix para links externos (rel)
function fix_external_links() {
	if (!document.getElementsByTagName) return;
	var anchors = document.getElementsByTagName("a");
	for (var i = 0; i < anchors.length; i++) {
		var anchor = anchors[i];
		if (anchor.getAttribute("rel") && anchor.getAttribute("rel") == "external") {
			anchor.target = "_blank";
		}
	}
}


function testearLleno(objeto,nombre) {
		if ($(objeto).val() == "") {
				openAlert('#msg-alertbox','El campo '+nombre+' no puede ser dejado en blanco.',objeto);
				return false;
		} else { 
				return true;
		}
}
function testearLlenoNum(objeto,nombre,mini,maxi){
		if($(objeto).val() == ""){
			openAlert('#msg-alertbox','El campo '+nombre+' no puede ser dejado en blanco.',objeto);
			return false;
		}else{
			if((mini > 0) && (maxi > 0)){
				if($(objeto).val().length >= mini && $(objeto).val().length <= maxi){
					var filtro = /^(?:\+|-)?\d+$/;
					if (!filtro.test($(objeto).val())) {
						openAlert('#msg-alertbox','El campo '+nombre+' debe ser num&eacute;rico.',objeto);
						return false;
					}else{
						return true;
					}
				} else { 
					if(mini == maxi){
						openAlert('#msg-alertbox','El campo '+nombre+' debe tener '+maxi+' d&iacute;gitos.',objeto);
					}else{
						openAlert('#msg-alertbox','El campo '+nombre+' debe tener entre '+mini+' y '+maxi+' d&iacute;gitos.',objeto);
					}
					return false;
				}
			}else{
				var filtro = /^(?:\+|-)?\d+$/;
				if (!filtro.test($(objeto).val())) {
					openAlert('#msg-alertbox','El campo '+nombre+' debe ser num&eacute;rico.',objeto);
					return false;
				}else{
					return true;	
				}
			}
		}
}

