function updateSucursales(provincia){
	$.get("ajax_sucursales.php", {Provincia: provincia}, function (data) {
		
		if(provincia == 'Capital Federal'){
			valor_retiro = "$ 9.90";	
		}else{
			valor_retiro = "$ 19.90";
		}
		$("#costo-envio span").html(valor_retiro);
		
		//actualizo subtotal
		var nuevo_subtotal = parseFloat($("#subtotal span").html().substr(2));
		
		//actualizo el total
		var envio = parseFloat($("#costo-envio span").html().substr(2));
		$("#total-compra span").html('$ '+(nuevo_subtotal+envio).toFixed(2));
		
		//actualizo el descuento, si hay codigo
		if($("#codigo-descuento").val()!=""){
			$.get("ajax_codigo_descuento.php", {Codigo: $("#codigo-descuento").val()}, function (data) {
					if(data.disponible == '1'){
						if(data.descuento != 0){
							var desc = ((parseFloat(($("#total-compra span").html()).substr(2))*data.descuento)/100).toFixed(2);
							$("#descuento-checkout span").html(data.descuento+'% ($ '+desc+')');
							$("#total-compra span").html('$ '+(parseFloat(($("#total-compra span").html()).substr(2))-desc).toFixed(2));
						}else{
							var desc = data.monto;
							$("#descuento-checkout span").html('$ '+data.monto);
							$("#total-compra span").html('$ '+(parseFloat(($("#total-compra span").html()).substr(2))-desc).toFixed(2));
						}
					}else{
						openAlert('#msg-alertbox','El c&oacute;digo ingresado no existe o ya fue utilizado con anterioridad.','#codigo-descuento');
					}
					
			},"json");
		}
		
		$("#listado-sucursales-data").html(data);
		
	});
}


